class Solution {
public:
    int mergesort(int l, int r, vector<int>&arr, vector<int>&temp)
    {
        if(l>=r)
        {
            return 0;
        }
        int mid = l+((r-l)/2);
        int ans = mergesort(l, mid, arr, temp);
        ans += mergesort(mid+1, r, arr, temp);
        ans += merge(l, r, arr, temp);
        return ans;
    }
    int merge(int l, int r, vector<int>&arr, vector<int>&temp)
    {
        int mid = l+((r-l)/2);
        int i=l, j=mid+1, k=l;
