class Solution {
    static int ft[];
    public int reversePairs(int[] nums) {
        int length_of_array=nums.length;
        //Calculating Size Of Fenwick (BIT) Tree
        int size=(int)Math.pow(2,(Math.ceil(Math.log(length_of_array)/Math.log(2.0))));
        ft=new int[size+1];
        return solve_using_Bit(nums);
    }
    public static int solve_using_Bit(int arr[])
    {
        int answer=0;
        int n= arr.length;;
        int sorted_Array[]=new int[n];
        for(int i=0;i<n;i++)
            sorted_Array[i]=arr[i];
        Arrays.sort(sorted_Array);
        for(int i=n-1;i>=0;i--)
        {
            //Search for half of current value and get its index from sorted array 
            int index=binary_search(arr[i]/2.0,sorted_Array);
            if(index!=-1) {
                index = sorted_Array[index] >= arr[i] / 2.0 ? index - 1 : index;
                if (index >= 0) {
                    int sum = sum_ft(index+1);
                    answer += sum;
                }
            }
            index=binary_search(arr[i],sorted_Array);
            update(index+1);
        }
        return answer;
    }
    public static int binary_search(double key1,int array[])
    {
        int low=0,high=array.length-1;
        int mid;
        int key=(int) key1;
        int pos=-1;
        while(low<=high)
        {
            mid=(low+high)/2;
            if(array[mid]<=key)
            {
                pos=mid;
                low=mid+1;
            }
            else
            {
                high=mid-1;
            }
        }
        return pos;
    }
    public static void update(int index)
    {
        int value=1;
        if (index!=0)
        while(index<ft.length)
        {
            ft[index]+=value;
            index+=Integer.lowestOneBit(index);
        }
    }
    public static int sum_ft(int index)
    {
        int sum=0;
        while(index>0)
        {
            sum+=ft[index];
            index-=Integer.lowestOneBit(index);
        }
        return sum;
    }
}
