    void merge_sort(vector<int> &v,int low,int high,int &c)
    {
        if(low<high)
        {   //cout<<low<<"  " <<high<<endl;
            int mid= low+((high-low)/2);
            merge_sort(v,low,mid,c);
            merge_sort(v,mid+1,high,c);
            c+=Merge(v,low,mid,high);
        }
    }
    int reversePairs(vector<int> &v) {
        int c{0};
        merge_sort(v,0,v.size()-1,c);
        return c;
    }
};
