    public void partition(int arr[],int low,int high,int[] ans){

        if(low<high){
            int mid=(low+high)/2;

            partition(arr,low,mid,ans);
            partition(arr,mid+1,high,ans);
            merge(arr,low,mid,high,ans);
        }

    }

    public void merge(int[] arr,int low,int mid,int high,int[] ans){

        int i=low,j=mid+1;

        while(i<=mid){

            while(j<=high && arr[i]>2*(long)arr[j])
                j +=1;

            ans[0] +=j-(mid+1);

            i +=1;
        }

        i=low;
        j=mid+1;
        int k=0;
        int temp[]=new int[high-low+1];

        while(i<=mid && j<=high){

            if(arr[i]<arr[j]){
                temp[k]=arr[i];
                i +=1;
                k +=1;
            }else{
                temp[k]=arr[j];
                j +=1;
                k +=1;
