func reversePairs(nums []int) int {
    // a shared buffer for intermediate sorted numbers
    var sorted = make([]int, len(nums))
    return divide(nums, sorted)
}

// the idea is to build two sorted subarrays recursively, and find the reverse pairs between two subarrays
func divide(nums []int, sorted []int) int {
    if len(nums) <= 1 {
        return 0
    }

    var l = 0
    var p = l + (len(nums)-1-l)/2 // the pivot(middle) point

    // the result should be the sum from the two subarrays, and the pairs across the two
    var ret = divide(nums[l:p+1], sorted) + divide(nums[p+1:], sorted)

    // index to check numbers in nums[p+1:] withg nums[l]
    var j = p+1
    var w = 0 // write index in `sorted`
    var r = p+1 // read index in the right half

    for ; l <= p; l++ {
        for j < len(nums) && nums[l] > nums[j] * 2 {
            j++
        }

        // As nums[0,p] and nums[p+1,] are both sorted, we know `nums[l] <= nums[l+1]`
        // - for the loop with l, we get nums[l] > any number in [p+1, p+1+j), and the number of pairs is `j-(p+1)`
        // - for the next loop with l+1, even j doesn't move, nums[l+1] >= nums[l] > any number in [p+1, p+1+j), we should count `j-(p+1)` for another time
        ret += j-(p+1)

        // put numbers that's not greater than nums[i] to the merge array
        for ;r < len(nums) && nums[l] >= nums[r];r++ {
            sorted[w] = nums[r]
            w++
        }

        sorted[w] = nums[l] // put nums[l] too
        w++
    }

    // there might be numbers left in the right half
    for ; r < len(nums); r++ {
        sorted[w] = nums[r]
        w++
    }

    copy(nums, sorted)

    return ret
}
