class Solution {
    class Pair implements Comparable<Pair>{
        long val;
        int idx;
        Pair(long val, int idx){
            this.val = val;
            this.idx = idx;
        }

        @Override
        public int compareTo(Pair obj){
            if(this.val == obj.val)return this.idx - obj.idx;
            if (this.val > obj.val) return 1;
            else if (this.val < obj.val) return -1;
            return 0;
        }
    }

    public int reversePairs(int[] nums) {
        TreeSet<Pair> set = new TreeSet<>();
        int ans = 0;
        for(int i = nums.length - 1; i>= 0; i--){
            int v = set.headSet(new Pair(nums[i],-1)).size();
            ans += v;
            set.add(new Pair(nums[i]*2L,i));
        }
        return ans;
    }
}
