//merge sort...
class Solution {

    int cnt = 0;            
    public int reversePairs(int[] nums) {
        if (nums == null || nums.length <= 1) return 0;
        int n = nums.length;
        int[] aux = new int[n];
        mergeSort(nums, 0, n - 1, aux);
        return cnt;
    }

    private void mergeSort(int[] nums, int lo, int hi, int[] aux) {
        if (lo == hi) { return; }
        int mid = lo + (hi - lo) / 2;
        mergeSort(nums, lo, mid, aux);
        mergeSort(nums, mid + 1, hi, aux);
        for (int i = lo; i <= hi; i++) {
            aux[i] = nums[i];
        }
        int j = mid + 1, t = mid + 1, k = lo;
        for (int i = lo; i <= mid; i++) {
            while (t <= hi && aux[i] > 2L * aux[t]) {
                //avoid integer overflow...
                t++;
            }
            cnt += t - mid - 1;
            while (j <= hi && aux[j] < aux[i]) {
                nums[k++] = aux[j++];
            }
            nums[k++] = aux[i];
        }
    }
}


