    int mergeSort(int[] a, int s, int e) {
        if(s >= e) return 0;
        int mid = s + (e - s) / 2;
        int count = mergeSort(a, s, mid) + mergeSort(a, mid + 1, e);
        int idx1 = s, idx2 = mid +1;
        while(idx1 <= mid && idx2 <= e) {
            while(idx2 <= e && a[idx1] > 2L * a[idx2]) idx2++;
            count += idx2 - mid -1;
            idx1++;
            if(idx2 > e && idx1 <= mid) count +=  (mid - idx1 + 1) * (idx2 - mid -1);
        }
        merge(a, s, mid, e);
        return count;
    }

    void merge(int[] a, int s, int m, int e) {
        int[] t = new int[e - s + 1];
        int idx = 0, start = s, s1 = m + 1;
        while(s <= m && s1 <= e) t[idx++] = a[s] <= a[s1] ? a[s++] : a[s1++];
        while(s <= m) t[idx++] = a[s++];
        while(s1 <= e) t[idx++] = a[s1++];
        for(int i = 0; i < idx; i++) a[start + i] = t[i];
    }
