    int merge(vector<int> &nums,int low,int mid,int high){
        int total=0;
        int j=mid+1;
        for(int i=low;i<=mid;i++){
            while(j<=high && (long)nums[i]>(long)2*nums[j]) j++;
            // STEP3
            // this will cnt the possible pairs 
            total+=j-(mid+1);
