        // STEP4
        // this is to merge the array
        vector<int> t;
        int left=low,right=mid+1;
        while(left<=mid && right<=high){
            if(nums[left]<=nums[right]){
                t.push_back(nums[left]);
                left++;
            }
            else{
                t.push_back(nums[right]);
                right++;
            }
        }
        while(left<=mid){
            t.push_back(nums[left]);
            left++;
        }
        while(right<=high){
            t.push_back(nums[right]);
            right++;
        }
        for(int i=low;i<=high;i++){
            nums[i]=t[i-low];
        }
