class Solution {
public:
    #define ll long long
    int merge(vector<int>&v,int l,int m,int h){
    int n1=m-l+1,n2=h-m;
    int left[n1],right[n2];
    for(int i=0;i<n1;i++){left[i]=v[l+i];}
    for(int i=0;i<n2;i++){right[i]=v[m+1+i];}
    int j=m+1;
    int res=0;
    for(int i=l;i<=m;i++){
        while(j<=h&&v[i]>2ll*v[j]){
            j++;
        }
        res+=j-(m+1);
    }
    j=0;
    int k=l,i=0;
    while(i<n1&&j<n2){
        if(left[i]<=right[j]){v[k]=left[i];i++;}
        else{v[k]=right[j];j++;}
        k++;
    }
    while(i<n1){v[k]=left[i];i++;k++;}
    while(j<n2){v[k]=right[j];j++;k++;}
    return res;                                                                                          
}
int count(vector<int>&v,int l,int h){
    int res=0;
    if(l<h){
        int m=l+(h-l)/2;
        res+=count(v,l,m);
        res+=count(v,m+1,h);
        res+=merge(v,l,m,h);
    }
    return res;
}
int reversePairs(vector<int>& nums) {
    int l=0,h=nums.size()-1;
    int ans=count(nums,l,h);
    return ans;
}
};
