class Solution:
    def reversePairs(self, nums: List[int]) -> int:
        if not nums or len(nums) == 0:
                return 
        tmp = [0 for _ in range(len(nums))]

        return self.merge_sort(nums, tmp, 0, len(nums) - 1)


    def merge_sort(self, nums, tmp, start, end):
        if start >= end:
            return 0

        mid = start + (end - start) // 2
        sum = 0

        sum = self.merge_sort(nums, tmp, start, mid) + \
              self.merge_sort(nums, tmp, mid + 1, end) + \
              self.find_reverse(nums, start, end) 

        self.merge(nums, tmp, start, end, mid)

        return sum


    def merge(self, nums, tmp, start, end, mid):
        l, r = start, mid + 1
        idx = start
        while l <= mid and r <= end:
            if nums[l] <= nums[r]:
                tmp[idx] = nums[l]
                l += 1
            else:
                # nums[l] > nums[r]
                tmp[idx] = nums[r]
                r += 1
            idx += 1

        while l <= mid:
            tmp[idx] = nums[l]
            l += 1
            idx += 1

        while r <= end:
            tmp[idx] = nums[r]
            r += 1
            idx += 1

        for i in range(start, end + 1):
            nums[i] = tmp[i]  

    def find_reverse(self, nums, start, end):
        res, mid = 0, (start + end) // 2

        j = mid + 1
        for i in range(start, j):
            while j <= end and nums[i] > 2 * nums[j]:
                res += mid - i + 1
                j += 1
        return res
