var longestPalindrome = function(s) {
    var newStr = "#" + s.split("").join("#") + "#";
    var dp = [], friendCenter = 0, friendRadius = 0, lpsCenter = 0, lpsRadius = 0;
    for (var i = 0; i < newStr.length; i++) {
        dp[i] = friendCenter + friendRadius > i ? Math.min(dp[friendCenter * 2 - i], (friendCenter + friendRadius) - i) : 1;
        while (i + dp[i] < newStr.length && i - dp[i] >= 0 && newStr[i + dp[i]] == newStr[i - dp[i]]) dp[i]++;
        friendRadius = friendCenter + friendRadius < i + dp[i] ? (function () {friendCenter = i; return dp[i];})() : friendRadius;
        lpsRadius = lpsRadius < dp[i] ? (function () {lpsCenter = i; return dp[i];})() : lpsRadius;
    }
    return s.substring((lpsCenter - lpsRadius + 1) / 2, (lpsCenter + lpsRadius - 1) / 2);
};
