                if(s[i] == s[j] && dp[i+1][j-1] == true)
                {
                    dp[i][j] = true;
                    max_length = max(k, max_length);
                    start_index = i;
                }
            }
        }
        string ans = "";
        // if there is no pallindromic substring present of size >= 2 
        if(max_length == 0)
        {
            ans += s[0];
            return ans;
        }
        for(int i = start_index; i < start_index + max_length; i++)
        {
            ans += s[i];
        }
        return ans;
    }
};

