class Solution {

    int max = Integer.MIN_VALUE;
    String ans = "";

    boolean recur(String s, int i, int j, int[][] dp){

        if(i>j) {   
            return true;
        }
        if(i==j) {
            dp[i][j] = 1;
            if(j-i+1 > max){
                max = j-i+1;
                ans = s.substring(i, j+1);
            }

            return true;
        }

        if(dp[i][j] != -1) return dp[i][j]==0?false:true;



        if(s.charAt(i)==s.charAt(j) && recur(s, i+1, j-1, dp)){

            dp[i][j] = 1;
            if(j-i+1 > max){
                max = j-i+1;
                ans = s.substring(i, j+1);
            }

        }
        else{
            dp[i][j] = 0;
            recur(s, i+1, j, dp);
            recur(s, i, j-1, dp);

        }

        return dp[i][j]==0?false:true;


    }



    public String longestPalindrome(String s) {


        int n = s.length();

        int[][] dp = new int[n+1][n+1];

        for(int i=0;i<=n;i++){
            Arrays.fill(dp[i], -1);
        }
        recur(s, 0, n-1, dp);

        return ans;

    }
}
