class Solution {
    public String longestPalindrome(String s) {
       int start = 0, end = 0;
       for(int i = 0; i < s.length(); i ++) {
           char c = s.charAt(i);
           int left = i;
           int right = i;
           while(left >= 0 && s.charAt(left) == c) left --;
           while(right < s.length() && s.charAt(right) == c) right ++;
