class Solution {

    public String longestPalindrome(String str) {

        int n = str.length();
        boolean[][] dp = new boolean[n][n];  //means if str.substring(i,j+1) is palindrome. 
        int maxLen=1,maxStart=0;
        //base
        for(int i=0;i<n;i++)
            dp[i][i] = true;

        for(int s = n-1;s>=0;s--) {
            for(int e=s+1;e<n;e++) {   
                if(str.charAt(s) ==str.charAt(e)) {
                    if(dp[s+1][e-1] || e-1-(s+1)+1==0 ) { // e-1-(s+1)+1==0 means smaller_str.length()==0
                        dp[s][e] = true;
                        int len = e-s+1;
                        if(maxLen<len) {
                           maxLen = len;
                           maxStart =s;
                        }
                    }
                }
            }
        }
        return str.substring(maxStart,maxStart+maxLen);
    }
} 
