class Solution {
    func longestPalindrome(_ s: String) -> String {
        var result = ""
        for index in 0 ..< s.count {
            let temp = getLongestPalindrome(i: index, s: s)
            if temp.count > result.count {
                result = temp
            }
        }
        return result
    }

    func getLongestPalindrome(i: Int, s: String) -> String {
        var left = i
        var right = i
        let charArr = Array(s)

        while (left - 1 >= 0 && charArr[left] == charArr[left - 1]) {
            left -= 1
        }

        while (right + 1 < charArr.count && charArr[right] == charArr[right + 1]) {
            right += 1
        }

        while (left - 1 >= 0 && right + 1 < charArr.count && charArr[left - 1] == charArr[right + 1]) {
            left -= 1
            right += 1
        }

        return String(charArr[left ... right])
    }
}
