```
char * longestPalindrome(char * s){
    // Dynamic Programming
    int n = strlen(s);
    // Default the begin and longest so that we can pick the first element if no palindrom longer than 1 charactor can be found
    int begin = 0;
    int longest = 1;
    // dp array
    bool dp[n][n];
    memset( dp, false, n*n*sizeof(bool) );

    // Base case: only one charactor
    for (int i = 0; i < n; i++)
    {
        dp[i][i] = true;
    }

    // Sub questions: substring with length of x
    for (int length = 2; length <= n; length++)
    {
        // Sub questions: substring with starting index of x
        for (int start = 0; start < n; start++)
        {
            int end = start + length - 1;
            // No need to check if end is out of bound
            if (end >= n)
            {
                break;
            }

            // Palindrom only exist when start and end are the same
            if (s[start] != s[end])
            {
                dp[start][end] = false;
            }
            else
            {
                // When length is 1 or 2, no need to check if it contains any palindrom substring
                if (length <= 2)
                {
                    dp[start][end] = true;
                }
                // Check if one index smaller at the beginning and the end is a palindrom substring
                else
                {
                    dp[start][end] = dp[start+1][end-1];
                }
            }
            // Check it the current substing is a palindrom
            if (dp[start][end])
            {
                // Compare the current substring with the longest
                if (length > longest)
                {
                    longest = length;
                    begin = start;
                }
            }
        }
    }
    // Get target substring
    char *result = malloc(n+1);
    strncpy(result, s+begin, longest);
    // Set the last index as ending char so that result is a string.
    result[longest] = 0;
    return result;

}
