class Solution {
    public String longestPalindrome(String s) {
        String res = "";
        int n = s.length();
        int reslen=0;
        for (int i=0;i<n;i++){
            // odd length sub strings 
            int l=i,r=i;
            while(l>=0 && r<n && s.charAt(l)== s.charAt(r)){
                if ((r-l+1)>reslen){
                    res=s.substring(l,r+1);
                    reslen=r-l+1;
                }
                l-=1;
                r+=1;
            }
            // even length sub strings 
            l=i;
            r=i+1;
            while(l>=0 && r<n && s.charAt(l)== s.charAt(r)){
                if ((r-l+1)>reslen){
                    res=s.substring(l,r+1);
                    reslen=r-l+1;
                }
                l-=1;
                r+=1;
            }

        }
        return res;
    }
}

