                board[row][index] = 'Q';
                placequeen(board , row + 1);
                board[row][index] = '.';
            }
        }
    }


    int totalNQueens(int n) {

         if(n <= 0)
            return res.size();

        vector<string> board(n,string(n,'.'));
        placequeen(board,0);

        return res.size();

