    bool isSafe(vector<vector<bool>>& board, int row, int col) {
	    int n = size(board);
	    for(int i = 0; i <= row; i++) {
		    if(board[i][col]) return false; 
		    if(row - i >= 0 && col - i >= 0 && board[row - i][col - i]) return false;
		    if(row - i >= 0 && col + i <  n && board[row - i][col + i]) return false;
	    }
	    return true;
