    int solve(vector<vector<bool>>& board, int row) {
	    if(row == size(board)) return 1;
	    int count = 0;
	    for(int col = 0; col < size(board); col++)           
		    if(isSafe(board, row, col)){          
			    board[row][col] = true;          
			    count += solve(board, row + 1);  
			    board[row][col] = false;         
		    }                                
	    return count;
    }
};
