class Solution {
public:
    void helper(int col, int n, vector<vector<string>>& ans , vector<string>& board, vector<int>& left, vector<int>& upperDigonal ,vector<int>& lowerDigonal)
    {
        if(col==n)
        {
            ans.push_back(board);
