class Solution {
public:
    int totalNQueens(int n) {
        int chessboard[9][9];
        memset(chessboard, -1, sizeof(chessboard));

        int total = 0;
        int row = 0;
        NQueens(chessboard, n, row, total);

        return total;
    }

    void NQueens(int chessboard[][9], int n, int row, int &total) {
        if(row == n) {
            total++;
            return;
        }

        for(int col = 0; col < n; ++col) {
            if(IsQueenSafe(chessboard, row, col, n) == true) {
                chessboard[row][col] = 0;
                NQueens(chessboard, n, row + 1, total);
                chessboard[row][col] = -1;
            }
        }
    }

    bool IsQueenSafe(int chessboard[][9], int row, int col, int n) {
        for(int i = row - 1, j = col; i >= 0; --i) {
            if(chessboard[i][j] == 0)
                return false;
        }

        for(int i = row - 1, j = col - 1; i >= 0 && j >= 0; --i, --j) {
            if(chessboard[i][j] == 0)
                return false;
        }

        for(int i = row - 1, j = col + 1; i >= 0 && j <= (n - 1); --i, ++j) {
            if(chessboard[i][j] == 0)
                return false;
        }
        return true;
    }
};
