      void recurse( vector<vector<string>> &vec ,vector<string> &board , int row , int &n)
    {

        if(row == n)
        {
            vec.push_back(board);
            return;
        }

        //ek particular row to already selected h and now for each cell we have to traverse through column 


        for(int col = 0 ; col < n ; col++)
        {
             if(isValid(board,row,col,n))
            {
                 board[row][col] = 'Q';
                 /*ek baar uss place pe Q ko rakh ke aage proceed kiya agar ispe se koi answer derive ho paega to vo apne aap end me base condition ki vajah se answer(vec) me pushback ho jaega ab backtrack karne ke baad iss postion se queen ko hatana bhi to padega taaki multiple configurations try kar sake   */
                 recurse(vec,board,row+1,n); // Recursing row by row
                 board[row][col] = '.';// yahan pe q ko hataya h 
            }
        }    
    }


    bool isValid(vector<string> &board ,int &row , int &col , int &n  )
    {

        for(int i=0;i<row;++i) // If column has queen already
        {
            if(board[i][col] == 'Q')
                return false;
        } 

        // If left diagonals have queen already 
        for(int i=1;row-i>=0&&col-i>=0;++i)
        {
            if(board[row-i][col-i] == 'Q')
                return false;
        }

        // If right diagonal have queen already
        for(int i=1;row-i>=0&&col+i<n;++i)
        {
            if(board[row-i][col+i] == 'Q')
                return false;
        }
        return true;
    }
