class Solution {
   public boolean issafe(int row,int col,char board[][]){
           //horizantal
           for(int j=0;j<board.length;j++){
               if(board[row][j]=='Q'){
                   return false;
               }
           }
           //vertical
           for(int i=0;i<board[0].length;i++){
               if(board[i][col]=='Q'){
                   return false;
               }
           }
           //upperleft
           int r=row;
           for(int c=col;c>=0&&r>=0;c--,r--){
               if(board[r][c]=='Q'){
                   return false;
               }
           }
           //upperright
            r=row;
           for(int c=col;c<board.length&&r>=0;c++,r--){
               if(board[r][c]=='Q'){
                   return false;
               }
           }
           //lowerleft
            r=row;
           for(int c=col;c>=0&&r<board[0].length;c--,r++){
                if(board[r][c]=='Q'){
                   return false;
               }
           }
           //lowerright
           return true;
       }
    public void combine(List<List<String>> list,char board[][]){
        List<String> currboard=new ArrayList<>();
        for(int row=0;row<board.length;row++){
            String curr="";
            for(int col=0;col<board[0].length;col++){
                if(board[row][col]=='Q'){
                    curr+='Q';
                }
                else{
                    curr+='.';
                }
            }
            currboard.add(curr);
        }
        list.add(currboard);
    }
    public void solve(List<List<String>> list,char board[][],int col){
        if(col==board.length){
            combine(list,board);
            return ;
        }
        for(int row=0;row<board.length;row++){
            if(issafe(row,col,board)){
                board[row][col]='Q';
                solve(list,board,col+1);
                board[row][col]='.';
            }
        }
    }
    public int totalNQueens(int n) {
        List<List<String>> list=new ArrayList<>();
        char board[][]=new char[n][n];
        solve(list,board,0);
        return list.size();
    }
}
```

