class Solution {
public:
    bool isCapital(char &c){
        return (c>='A' && c<='Z');
    }

    bool isLower(char &c){
        return (c>='a' && c<='z');
    }

    bool isEveryLetterLower(int start,int end,string &word){
        while(start<end){
            if(!isLower(word[start++]))
                return false;
        }
        return true;
    }

    bool isEveryLetterUpper(int start,int end,string &word){
        while(start<end){
            if(!isCapital(word[start++]))
                return false;
        }
        return true;
    }

    bool detectCapitalUse(string &word) {
        int n = word.size();
        if(n == 1){
            return true;
        }
        if(isCapital(word[0])){
            if(isEveryLetterLower(1,n,word)){
                return true;
            }else if(isEveryLetterUpper(1,n,word)){
                return true;
            }
        }else{
            if(isEveryLetterLower(1,n,word)){
                return true;
            }
        }
        return false;
    }
};
```

