class Solution {
public:
    bool detectCapitalUse(string word) {
         if((word[0]>=97 && word[0]<=122) && (word[1]>=65 && word[1]<=96)) return false;
        if(word.size()==1)  return true;
        for(int i=2; i<word.length(); i++)
        {
            // CASE 1//
            if((word[0]>=65 && word[0]<=96) && (word[1]>=65 && word[1]<=96))
                if((word[i])>=96 && word[i]<=122)  return false;
             // CASE 2//
             if((word[0]>=65 && word[0]<=96) && (word[1]>=97 && word[1]<=122) )
