class Solution {
public:
    bool detectCapitalUse(string word) {
        if(word.length() == 1)
            return true;
        int capitalCount = 0; // counting number of capital letter
        for(char ch : word){
            if(ch >= 'A' && ch <= 'Z')
                capitalCount++;
        }
        // for handling "USA" type of word
        if(capitalCount == word.length())
            return true;
        // for handling "leetcode" type of word
        else if(capitalCount == 0)
            return true;
        // for handling "Google" type of word
        else if(capitalCount == 1 && (word[0] >= 'A' && word[0] <= 'Z'))
            return true;
        else
            return false;

    }
};
