    public bool DetectCapitalUse(string word) {
        TextInfo TI = new CultureInfo("en-US",false).TextInfo;

        if(TI.ToTitleCase(word) == word){
            return true;
        }
        else if(word.Any(char.IsUpper) && word.Any(char.IsLower)){
            return false;
        }
        return true;
    }
}
