class Solution {
    // TC: O(n) n is length of word
    // SC: O(1)
    public boolean detectCapitalUse(String word) {
        int numUpperCaseChars = 0;

        for (int idx = 0; idx < word.length(); idx++) {
            char c = word.charAt(idx);
            if (Character.isUpperCase(c)) {
                numUpperCaseChars++;
            }

            if (numUpperCaseChars > 1 && numUpperCaseChars < idx + 1) return false;
        }

        if (numUpperCaseChars == 1 && !Character.isUpperCase(word.charAt(0))) return false;

        return true;
    }
}
