class Solution {
public:
    bool detectCapitalUse(string word) {
        int count=0;
        if(word.size()==1)
        {
            return true;
        }
        for(int i=0;i<word.size();i++)
        {
            if(isupper(word[i]))
            {
                count++;
            }
        }
        if(count==1 && isupper(word[0]))
        {
            return true;
        }
        else if(count==0 || count==word.size())
        {
            return true;
        }
        else
        {
            return false;
        }
    }
};

class Solution {
    public boolean detectCapitalUse(String word) {
        int U=0;
        for(int i=0; i<word.length()-1; i++)
        {
            char ch = word.charAt(i);

            if(Character.isUpperCase(ch)) //Character.isUpperCase(val)
            U++;
        }

        if(U == word.length()-1 || (Character.isUpperCase(0) && word.length()-1-U == word.length()-2 ) || U == 0)
        return true;

        return false;

    }
}

