class Solution {
public:
    int maxi(int a,int b){
        return (a>b)? a:b;
    }
    int mini(int a,int b){
        return (a<b)? a:b;
    }
    int checksubseq(string a,string b){
        int l1= a.length();
        int l2= b.length();
        int arr[l1+1][l2+1];
        for(int i=0;i<=l1;i++){
            for(int j=0;j<=l2;j++){
                if(i==0 || j==0)
                    arr[i][j]=0;
                else if(a[i-1]==b[j-1])
                    arr[i][j]= 1 + arr[i-1][j-1];
                else
                    arr[i][j]= maxi(arr[i-1][j],arr[i][j-1]);
            }
        }
        return arr[l1][l2];
    }
    int findLUSlength(string a, string b) {
        if(a==b)
            return -1;
        int l= checksubseq(a,b);
        int k= mini(a.length(),b.length());
        if(l==1)
          return maxi(a.length(),b.length());  
        if(l==k)
            return (maxi(a.length(),b.length())-l);
        return maxi(a.length(),b.length());
    }
};
```

