class Solution {
    public int findLUSlength(String a, String b) {
        if(a.length()>b.length()){
            if(a.contains(b+" ")){
                return a.length()-b.length();
            }else{
                return a.length();
            }
        }
        else if(b.length()>a.length()){
            if(b.contains(a+" ")){
                return b.length()-a.length();
            }else{
                return b.length();
            }
        }
        else if(a.length()==b.length()){
            if(a.contains(b)){
                return -1;
            }
        }
        return a.length();
    }
}
