        int ans = helper(a, 0, b, 0);
        if (ans == 0) return -1;
        return ans;
    }
    private int helper(String a, int i, String b, int j) {
        if (a.isEmpty() || b.isEmpty()) return Math.max(i + a.length(), j + b.length());
        if (a.charAt(0) == b.charAt(0)) return helper(a.substring(1), i, b.substring(1), j);
        return Math.min(helper(a, i, b.substring(1), j + 1), helper(a.substring(1), i + 1, b, j));
    }
