class Solution {
public:
    //[-2,1,-3,4,-1,2,1,-5,4]
    // 0, 1, 2, 3, 4, 5, 6
    // 0, 1,
    //
    //
    int maxSubArray(vector<int>& nums) {
        const int size = nums.size();
        vector<int> dp(size, 0);        
        // recrrence relation
        // dp[i+1] = dp[i] + max(nums[i+1],0) + min(0, nums[i])
        dp[0] = max(nums[0], 0);
        for(int i = 1; i < nums.size(); ++i) {
            dp[i] = dp[i-1] + max(nums[i-1], 0) + min(0, nums[i-1]);
        }

        return dp[size-1];
    }
};
