public void nextPermutation(int[] num) {
  // Find the first place moving from right to left that is not assending
  int dest = num.length - 2;
  for (; 0 <= dest && num[dest] >= num[dest + 1]; --dest)
    ;

  // If we found a location that is not sorted
  if (0 <= dest) {
    // Find the right most value greater than our destination (there must be one)
    int target = num.length - 1;
    for (; num[dest] >= num[target]; --target)
      ;

    // Swap the destination with the target
    int tmp = num[dest];
    num[dest] = num[target];
    num[target] = tmp;
  }

  // Reverse everything right of the destination
  for (int end = num.length - 1; dest + 1 < end; ) {
    int tmp = num[++dest];
    num[dest] = num[end];
    num[end--] = tmp;
  }
}
