// Algorithm
//  1. find index i that arr[i] < arr[i+1] from end-1 to start of array.
//  2. Search for index j that arr[j] > arr[i] from back to start.
//  3. Swap (arr[i] > arr[j]).
//  4. Reverse Array from index i+1 to End of Array.
//  5. In Case we can not find i that means it is a Sorted Array(decreasing) , We have simply reverse Array.
class Solution
{
public:
    void nextPermutation(vector<int> &arr)
    {
        int firstIndex = INT_MAX;
        for (int i = arr.size() - 2; i >= 0; i--)
        {
            if (arr[i] < arr[i + 1])
            {
                firstIndex = i;
                break;
            }
        }
        if (firstIndex == INT_MAX)
        {
            reverse(arr.begin(), arr.end());
        }
        else
        {
            int highIndex = INT_MAX;
            for (int i = arr.size() - 1; i >= 0; i--)
            {
                if (arr[i] > arr[firstIndex])
                {
                    highIndex = i;
                    break;
                }
            }
            swap(arr[highIndex], arr[firstIndex]);
            reverse(arr.begin() + firstIndex + 1, arr.end());
        }
    }
};
```

