class Solution {
public:
    void nextPermutation(vector<int>& nums) {
        int index1=-1,index2=-1;
        for(int i=nums.size()-1;i>0;i--) {
            if(nums[i]>nums[i-1]) {
                index1=i-1;
                break;
            }

