class Solution {
    public void nextPermutation(int[] arr) {
        int n = arr.length;
        if (n == 0) return;
        int i = n - 2;
        while (i >= 0 && arr[i] >= arr[i+1]) i--; // Find first min point from the right
        if (i == -1) {
            reverse(arr, 0, n - 1);
        } else {
            int nextGreaterIndex = i;
            for (int j = i + 1; j < n; j++) {
                if (arr[j] > arr[i])
                    nextGreaterIndex = j;
            }
            swap(arr, i, nextGreaterIndex);
            reverse(arr, i + 1, n - 1);
        }
    }
    void swap(int[] arr, int i, int j) {
        int tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }
    void reverse(int[] arr, int l, int r) {
        while (l < r) swap(arr, l++, r--);
    }
}
