class Solution {
    public void nextPermutation(int[] arr) {
        int n = arr.length;
        int ind = -1;
        // for finding the break point
        for (int i = n - 2; i >= 0; i--) {
            if (arr[i] < arr[i + 1]) {
                ind = i;
                break;
            }
        }
        // if the break point does not exist
        if (ind == -1) {
            reverse(arr, 0, n - 1);
            return;
        }
        // if the break point exists
        int j = n - 1;
        for (int i = n - 1; i >= ind + 1; i--) {
            if (arr[i] > arr[ind]) {
                swap(arr, i, ind);
                break;
            }
        }
        // Reverse the entire right half array
        reverse(arr, ind + 1, n - 1);
