public void nextPermutation(int[] nums) {
    // Degenerate case.
    if (nums.length < 2) {
        return;
    }

    // Step 0: Working from right to left, find the index i of the first decreased value.
    int indexOfFirstDecrease;
    indexOfFirstDecrease = nums.length - 2;
    while (indexOfFirstDecrease >= 0 && nums[indexOfFirstDecrease] >= nums[indexOfFirstDecrease + 1]) {
        indexOfFirstDecrease--;
    }

    // Step 1: If it's a strictly decreasing array, there is no greater permutation, so we need to find the least
    // according to the problem statement. We can do this by reversing the array so it is strictly increasing.
    // Then we're finished.
    if (indexOfFirstDecrease == -1) {
        for (int j = 0; j < nums.length / 2; j++) {
            int temp = nums[j];
            nums[j] = nums[nums.length - 1 - j];
            nums[nums.length - 1 - j] = temp;
        }
        return;
    }

    // Step 2: Find next-greatest number in the subpermutation after nums[indexOfFirstDecrease];
    // This works because we know that nums[i + 1], ..., nums[nums.length - 1] are in strictly non-increasing order
    int indexOfNextGreatestNumber = indexOfFirstDecrease + 1;
    while (indexOfNextGreatestNumber < nums.length - 1 && nums[indexOfNextGreatestNumber + 1] > nums[indexOfFirstDecrease]) {
        indexOfNextGreatestNumber++;
    }

    // Step 3: Interchange the values at indexOfFirstDecrease and indexOfNextGreatestNumber.
    int interchangeTemp = nums[indexOfFirstDecrease];
    nums[indexOfFirstDecrease] = nums[indexOfNextGreatestNumber];
    nums[indexOfNextGreatestNumber] = interchangeTemp;

    // Step 4: Reverse the values between indices (indexOfFirstDecrease + 1) and (nums.length - 1)
    int init = indexOfFirstDecrease + 1;
    for (int k = 0; k < (nums.length - init) / 2; k++) {
        int temp = nums[init + k];
        nums[init + k] = nums[nums.length - 1 - k];
        nums[nums.length - 1 - k] = temp;
    }
}

