       while (start > 0 && num[start-1] >= num[start]) start--;
       if (start > 0) {
           int end = num.length - 1;
           while (num[end] <= num[start-1]) end--;
           swap(num, start-1, end);
       }
       //we don't need to sort, we can just reverse it
       for (int i=start; i<(start+num.length)/2; i++) {
           swap(num, i, num.length-1+start-i);
       }
    }

    void swap(int[] A, int i, int j) {
        int temp = A[i];
        A[i] = A[j];
        A[j] = temp;
