public class Solution {
    public void NextPermutation(int[] nums) {
        var n = nums.Length;
        // 1. find the last element of the increasing sequence from the back to the front
        var lastElementIndex = n - 1;
        while (lastElementIndex > 0 && nums[lastElementIndex - 1] >= nums[lastElementIndex]) {
            lastElementIndex--;
        }

        var preLastElementIndex = lastElementIndex - 1;

        if (preLastElementIndex < 0) {
            // Corner case: 4,3,2,1 => 1,2,3,4
            reverse(0, n - 1, nums);
        } else {
            // 2. find the fist element larger than the pre and replace from the back to the front
            var firstElementLargerIndex = n - 1;
            while (firstElementLargerIndex > 0 && nums[preLastElementIndex] >= nums[firstElementLargerIndex]) {
                firstElementLargerIndex--;
            }

            // 3. swap
            var temp = nums[firstElementLargerIndex];
            nums[firstElementLargerIndex] = nums[preLastElementIndex];
            nums[preLastElementIndex] = temp;

            // 4. reverse [lastElementIndex ... n - 1] in place
            reverse(lastElementIndex, n - 1, nums);
        }
    }

    private void reverse(int left, int right, int[] nums) {
        while (left < right) {
            var temp = nums[left];
            nums[left] = nums[right];
            nums[right] = temp;
            left++;
            right--;
        }
    }
}
