        int n = nums.length;
        for(int i=n-2;i>=0;i--){
            if(nums[i] < nums[i+1]){
                index= i;
                break;
            }
        }
        if(index==-1){
            reverse(nums,0,n-1);
        }
        else{
            for(int i=n-1;i>index;i--){
                if(nums[i] > nums[index]){
                    swap(nums,i,index);
                    break;
                }
            }
            reverse(nums,index+1, n-1 );
        }

    }
}
