void nextPermutation(vector<int>& nums) {
	// start from end
	for (int i=nums.size()-1; i>=1; i--){ 
			// if there is an number that is smaller than the next number, that means we can start swapping
			if (nums[i]>nums[i-1]){
				// we search for the smallest number(but larger than current number) in the remaining vector and swap
				for (int j=nums.size()-1; j>=i; j--){
					if (nums[j]>nums[i-1]){
						swap(nums[i-1],nums[j]);
						// sort the remaining vector so its smallest possible with index i being the new number
						sort(nums.begin()+i,nums.end());
						return;
					}
				}

			}
		}
	sort(nums.begin(),nums.end());
}
