class Solution {

    func nextPermutation(_ nums: inout [Int]) {

        if nums.count <= 0 { return }

        var switchIndex: Int?
        var prev = nums.last!
        var i: Int = nums.count - 2

        while i >= 0 {    
            if nums[i] < prev {
                switchIndex = i
                break
            }
            prev = nums[i]
            i -= 1
        }

        guard let index = switchIndex else {
            nums.reverse()
            return
        }

        var successorValue: Int?
        var successorIndex: Int = index + 1

        for i in index+1..<nums.count {
            if nums[i] > nums[index] {
                if let value = successorValue {
                    if nums[i] <= value {
                        successorValue = nums[i]
                        successorIndex = i
                    }
                } else {
                    successorValue = nums[i]
                    successorIndex = i
                }
            }
        }

        nums.swapAt(successorIndex, index)
        nums[(index+1)...].reverse()
    }
}
