var nextPermutation = function(nums) {
    function swap(a, b) {
        const temp = nums[a]
        nums[a] = nums[b]
        nums[b] = temp
    }

    function reverse(start) {
        let end = nums.length - 1
        while (start < end) {
            swap(start, end)
            start++
            end--
        }
