class Solution {
    public void nextPermutation(int[] nums) {

       int x = nums.length-1;

       while(x>0 && nums[x]<=nums[x-1])
       {
           x--;
       }
       //System.out.println(nums.length-1+" "+x);
       if(x-1>=0){
       for(int i=nums.length-1;i>=x;i--)
       {
           if(nums[i]>nums[x-1])
           {
               int temp = nums[x-1];
               nums[x-1] = nums[i];
               nums[i] = temp;
               break;
           }
       }
       }
       int i=x;
       int j=nums.length-1;

       while(i<j)
       {
           int temp = nums[i];
           nums[i] = nums[j];
           nums[j] = temp;
           i++;
           j--;
       }

       for(int i1=0;i1<=nums.length-1;i1++)
       {
           System.out.print(nums[i1]+" ");
       }

    }
}

